'use strict';

var through = require('through2');
var gutil = require('gulp-util');
var ejs = require('ejs');
var fs = require('fs');

module.exports = function (options, settings) {
    settings = settings || {};
    options = options || {};
    settings.ext = typeof settings.ext === "undefined" ? ".html" : settings.ext;

    return through.obj(function (file, enc, cb) {
        if (file.isNull()) {
            this.push(file);
            return cb();
        }

        if (file.isStream()) {
            this.emit(
                'error',
                new gutil.PluginError('gulp-ejs', 'Streaming not supported')
            );
        }

        options.filename = file.path;
        try {
            console.log(options.filename);
            file.path = gutil.replaceExtension(file.path, settings.ext);

            var jsonData = JSON.parse(fs.readFileSync(file.path.replace(settings.ext,'.json')));
            for(var k in jsonData) {
                if(options[k] == undefined) {
                    options[k] = jsonData[k];
                }  
            }

            file.contents = new Buffer(
                ejs.render(file.contents.toString(), file.data || options)
            );

        } catch (err) {
            this.emit('error', new gutil.PluginError('gulp-ejs', err.toString()));
        }

        this.push(file);
        cb();
    });
};
